import styled from 'styled-components/macro'

export const DropdownContainer = styled.div`
    border: 1px solid #ccc;
    padding: 6px;
    border-radius: 4px;

    //for tablets and desktops
    @media(min-width:576px){
        justify-self: end;
    }
`
